import connection from '../configs/connectDB';
let md5 = require('md5');

// connection.execute('SELECT * FROM `user` WHERE `phone_login` = ? AND `password_login` = ?', [phone_login, password_login], function(err, result, fields) {
//     if (result.length > 0) {
//         res.end('{"message": true, "status": 200 }');
//     } else {
//         res.end('{"message": false, "status": 500 }');
//     }
// });

// trang login
const getPageLogin = (req, res) => {
    return res.render('login/index.ejs');
}

// Đăng Nhập
const loginFunc = async(req, res) => {
    let phone_login = req.body.phone_login;
    let password_login = md5(req.body.password_login);
    // 1. thành công
    // 2. không tồn tại
    // 3. sai mật khẩu
    if (phone_login && password_login) {
        connection.execute('SELECT * FROM `users` WHERE `phone_login` = ? AND `veri` = 1', [phone_login], function(err, result, fields) {
            if (result.length == 0) {
                res.end('{"message": 2}');
            } else if (result.length == 1) {
                if (result[0]['password_v1'] != password_login) {
                    res.end('{"message": 3}');
                } else {
                    res.end('{"message": 1}');
                }
            }
        });
    }
}

// Gửi OTP
const sendOTP = (req, res) => {
    let phone_signup = req.body.phone_signup;
    let ip = req.body.ip;
    let otp = Math.floor(Math.random() * (999999 - 100000)) + 100000;
    if (phone_signup) {
        connection.execute('SELECT * FROM `users` WHERE `phone_login` = ?', [phone_signup], (err, result, fields) => {
            if (result.length == 0) {
                connection.execute('INSERT INTO `users` SET `phone_login` = ?, `ip` = ?, `otp` = ?', [phone_signup, ip, otp]);
            } else if (result.length != 0) {
                connection.execute('UPDATE `users` SET `sented` = 0, `otp` = ? WHERE `phone_login` = ? ', [otp, phone_signup]);
            }
        });
    }
}

// Đăng ký
const register = (req, res) => {
    // 1. thành công
    // 2. không tồn tại
    // 3. sai mật khẩu
    function readableRandomStringMaker(length) {
        for (var string = ''; string.length < length; string += 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789'.charAt(Math.random() * 62 | 0));
        return string;
    }
    var id_user = readableRandomStringMaker(16);
    var phone_signup = req.body.phone_signup;
    var password_v1 = md5(req.body.password_v1);
    var codeOTP = req.body.codeOTP;
    var MaGioiThieu = req.body.MaGioiThieu;
    var otp = Math.floor(Math.random() * (999999 - 100000)) + 100000;
    let MaGioiThieu_User = Math.floor(Math.random() * (999999 - 100000)) + 100000;
    let name_user = "Member" + phone_signup.substring(5);
    const TimeCreate = () => {
        var arr = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'June', 'July', 'Aug', 'Sept', 'Oct', 'Nov', 'Dec'];
        const dateNow = new Date();
        var day = dateNow.getDate() < 10 ? "0" + dateNow.getDate() : dateNow.getDate();
        var month = arr[dateNow.getMonth()];
        var year = dateNow.getFullYear();
        var hour = dateNow.getHours() < 10 ? "0" + dateNow.getHours() : dateNow.getHours();
        var minute = dateNow.getMinutes() < 10 ? "0" + dateNow.getMinutes() : dateNow.getMinutes();
        var time = hour + ":" + minute;
        var am_pm = "";
        if (dateNow.getHours() >= 12) {
            am_pm = "pm";
        } else {
            am_pm = "am";
        }
        return day + " " + month + " " + year + ", " + time + " " + am_pm;
    }

    var timeCr = TimeCreate();

    if (phone_signup) {
        connection.execute("SELECT * FROM `users` WHERE `phone_login` = ? ", [phone_signup], (err, result) => {
            if (result[0]['veri'] == 1) {
                res.end('{"message": 0}');
            } else {
                if (result[0]['otp'] != codeOTP) {
                    res.end('{"message": 2}');
                } else {
                    connection.execute("SELECT * FROM `users` WHERE ma_gt = ? ", [MaGioiThieu], (err, result) => {
                        if (result.length == 1) {
                            var sql = 'UPDATE `users` SET `id_user` = ?, `password_v1` = ?, `name_user` = ?, `ma_gt` = ?, `ma_gt_f1` = ?, `veri` = 1, `otp` = ?, `time` = ? WHERE `phone_login` = ? ';
                            connection.execute(sql, [id_user, password_v1, name_user, MaGioiThieu_User, MaGioiThieu, otp, timeCr, phone_signup]);
                            res.end('{"message": 1}');
                        } else {
                            res.end('{"message": 3}');
                        }
                    });
                }
            }
        });
    }
}


module.exports = {
    getPageLogin,
    loginFunc,
    sendOTP,
    register
}

