    if (couleepays.length > 0) {
        await connection.execute('UPDATE `recharge` SET `loai` = ?, `timeEnd` = ? WHERE `phone_login` = ? AND id_txn = ? ', [typeS, endTime, phone_login, id_don_hang]);
