    const sql3 = 'UPDATE `temp` SET `ket_qua` = ?';
    await connection.execute(sql2, [update_kq, time]);
    await connection.execute(sql3, [0]);
    await connection.execute(sql, [giai_doan_moi, cau_moi, time]);
    handlingOrder();
}

module.exports = {
    add_tage_woipy,
    handlingOrder,
}

